mapper.select:
SELECT DATE(create_time) AS date, IFNULL(SUM(total_amount), 0) AS totalSales, COUNT(*) AS orderCount FROM `order` WHERE status IN (1, 2, 3)
AND DATE(create_time) >= #{startDate}
AND DATE(create_time) <= #{endDate}
GROUP BY DATE(create_time) ORDER BY date

SELECT DATE(create_time) AS date, COUNT(*) AS userCount FROM user WHERE role = 0
AND DATE(create_time) >= #{startDate}
AND DATE(create_time) <= #{endDate}
GROUP BY DATE(create_time) ORDER BY date
